sys_ui_policy.script_false:
function onCondition() {
    g_form.setMandatory('description', false);
    g_form.setReadOnly('short_description', false);
}

sys_ui_policy.script_true:
function onCondition() {
    // get the sys_id of the current record
    var sys_id = g_form.getUniqueValue();

    // create a GlideRecord object for the incident table
    var gr = new GlideAjax('x_58872_needit.CallData1');
    gr.addParam('sysparm_name', 'rState');
    gr.addParam('sys_id', sys_id);

    gr.getXMLAnswer(res);

    function res(response) {
		console.log(response)
        if (response == 0) {
            g_form.setMandatory('description', true);
            g_form.setReadOnly('short_description', true);
        }else{
			g_form.setMandatory('description', false);
			g_form.setReadOnly('short_description',false);
		}
    }


    // if a record is found, retrieve the 'priority' value and set the field visibility based on that value

}
